import jwtParse from "jwt-decode";
import config from "./configServer.json";
import { ICategory, IProduct, IRegisterData, IUser } from "./types";

export const signUp = async (data: IRegisterData) => {
  const r = await fetch(config.endPoint + "/auth/sign-up", {
    method: "POST",
    headers: {
      "Content-Type": "application/json;charset=utf-8",
    },
    body: JSON.stringify(data),
  });
  const json = await r.json();
  // получаем jwt и добываем из него User(убрать лишнее)
  const jwtData: IUser = jwtParse(json.refreshToken);

  const user = {
    email: jwtData.email,
    name: jwtData.name,
    isAdmin: jwtData.isAdmin,
  };
  return user;
};

export const getCategories = async (): Promise<ICategory[]> => {
  const r = await fetchWithToken(config.endPoint + "/category", {
    method: "GET",
  });
  return r.json();
};

export const getProducts = async (): Promise<IProduct[]> => {
  const r = await fetchWithToken(config.endPoint + "/product", {
    method: "GET",
    headers: {},
  });
  return r.json();
};

const fetchWithToken = (url: string, options?: RequestInit | undefined) => {
  const token = localStorage.getItem("token");
  if (token === null) {
    throw new Error("Unauthorized");
  }
  const { headers = {}, ...restOfOptions } = options || {};

  return fetch(url, {
    headers: {
      Authorization: `Bearer ${token}`,
      ...headers,
    },
    ...restOfOptions,
  });
};
