import "./product.css";
import { IProduct } from "../../types";
import { useParams } from "react-router-dom";

interface IProps {
  product: IProduct;
}

interface IProductId {
  productId: string;
}

export const Product = (props: IProps) => {
  const { productId } = useParams<IProductId>();
  return (
    <>
      <div className="product-card">
        <img
          className="product-card__image"
          src={props.product.image}
          alt="Картинка"
        ></img>
        <span className="product-card__name">{props.product.name}</span>
        <span className="product-card__price">{props.product.price}</span>
        <button className="product-card__button">Открыть-карточку</button>
      </div>
    </>
  );
};
