import { useState } from "react";
import { useAppSelector } from "../../store";
import { IProduct } from "../../types";
import { Product } from "./Product";

export const ProductsList = () => {
  const products = useAppSelector((state) => state.products);
  const [product, setProduct] = useState<IProduct[]>(products);

  const handleSortByAscending = (items: IProduct[]) => {
    const sortedItems = items.sort((a, b) => b.price - a.price);
    setProduct(prevState => sortedItems);
    console.log("HIGH", sortedItems);
  };

  const handleSortByDescending = (items: IProduct[]) => {
    const sortedItems = items.sort((a, b) => a.price - b.price);
    setProduct(sortedItems);
    console.log("LOW", sortedItems);
  };

  return (
    <div>
      <button onClick={() => handleSortByAscending(products)}>High</button>
      <button onClick={() => handleSortByDescending(products)}>Low</button>
      {product.map((product) => (
        <Product key={product._id} product={product} />
      ))}
    </div>
  );
};
