import "./product.css";
import { IProduct } from "../../types";

interface IProps {
  product: IProduct;
}

export const Product = (props: IProps) => {
  return (
    <>
    <div className="card-product">
      <img className="card-product__image" src={props.product.image} alt="Картинка"></img>
      <span className="card-product__name">{props.product.name}</span>
      <span className="card-product__price">{props.product.price}</span>
    </div>
    <button>Открыть-карточку</button>
    </>
    
  );
};
