import { useAppSelector } from "../store";
import { IProduct } from "../types";

export const Basket = () => {
  const products = useAppSelector((state) => state.products);
  const productsIdInBasket = useAppSelector(
    (state) => state.productInBasketIdList
  );

  const filteredProductsByIdToBasket = products.reduce(
    (newArr: IProduct[], product) => {
      productsIdInBasket.filter((id) => {
        if (id === product._id) {
          newArr = [...newArr, product];
        }
        return newArr;
      });
      return newArr;
    },
    []
  );
  localStorage.setItem("productsInBasket", JSON.stringify(filteredProductsByIdToBasket));
  console.log(filteredProductsByIdToBasket);

  return (
    <div>
      <span>Basket</span>
    </div>
  );
};

// ({ productInBasket }: { productInBasket: string[] })
