import { useAppDispatch } from "../../store";
import { setSelectedCategoryAction } from "../../store/reducers/reducer";
import { ICategory } from "../../types";

interface IProps {
  category: ICategory;
}

export const Category = (props: IProps) => {
  const dispatch = useAppDispatch();
  const handleSelectedCategory = (id: string) => {
    dispatch(setSelectedCategoryAction(id));
  };
  return (
    <button className="category-list__button" onClick={() => handleSelectedCategory(props.category._id)}>
      {props.category.name}
    </button>
  );
};
