import { useAppDispatch, useAppSelector } from "../../store";
import { setProductsAction } from "../../store/reducers/reducer";
import { IProduct } from "../../types";
import { Product } from "./Product";

export const ProductsList = () => {
  const products = useAppSelector((state) => state.products);
  const sortBy = useAppSelector((state) => state.sortBy);
  const dispatch = useAppDispatch();

  const handleSortByAscending = () => {
    if (sortBy === "asc") {
      products.sort((a, b) => b.price - a.price);
    }
    dispatch(setProductsAction(products));
  };

  const handleSortByDescending = () => {
    products.sort((a, b) => a.price - b.price);
  };

  return (
    <div>
      <button onClick={() => handleSortByAscending()}>High</button>
      <button onClick={() => handleSortByDescending()}>Low</button>
      {products.map((product) => (
        <Product key={product._id} product={product} />
      ))}
    </div>
  );
};
