import React from "react";
import { useForm } from "react-hook-form";

interface IFormInput {
  login: string;
  password: string;
  firstName: string;
  gender?: string;
  isAdmin: boolean;
}

const RegisterForm = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm<IFormInput>();
  const submitFunc = (data: IFormInput) => {
    console.log(data);
  };

  return (
    <>
      <h1>Регистрация</h1>

      <form onSubmit={handleSubmit(submitFunc)}>
      <label className="label">
          Имя
          <input
            type="text"
            placeholder="Email"
            {...register("firstName", { required: true, minLength: 3 })}
          />
        </label>
        <label className="label">
          Электронная почта
          <input
            type="text"
            placeholder="Email"
            {...register("login", { required: true, minLength: 5 })}
          />
        </label>
        <p className="error">{errors.login && "Email адрес обязателен"}</p>
        <label className="label">
          Пароль
          <input type="text" {...register("password")} />
        </label>
        <input type="hidden" value="false" {...register("isAdmin")}></input>
        <input type="submit" />
      </form>
    </>
  );
};

export default RegisterForm;
