import { useForm } from "react-hook-form";
import { IRegisterData, IUser } from "./types";
import config from "./configServer.json";
import jwtParse from "jwt-decode";
import { useAppDispatch, useAppSelector } from "./store";
import { setUserAction } from "./store/reducers/reducer";
import { Main } from "./components/Main";

const RegisterForm = () => {
  const dispatch = useAppDispatch();
  const user = useAppSelector((state) => state.user);
  const {
    register,
    handleSubmit,
    reset,
    formState: { errors, isValid },
  } = useForm<IRegisterData>({ mode: "onChange" });

  const submitFunc = async (data: IRegisterData) => {
    // запрос sign-up / sign-in
    try {
      const response = await fetch(config.endPoint + "/auth/sign-up", {
        method: "POST",
        headers: {
          "Content-Type": "application/json;charset=utf-8",
        },
        body: JSON.stringify(data),
      });
      const json = await response.json();
      // получаем jwt и добываем из него User(убрать лишнее)
      const jwtData: IUser = jwtParse(json.refreshToken);

      const user = {
        email: jwtData.email,
        name: jwtData.name,
        isAdmin: jwtData.isAdmin,
      };
      console.log(user);

      // сохраняем jwt в localstorage
      localStorage.setItem("token", json.refreshToken);
      dispatch(setUserAction(user));
      reset();
    } catch (error) {
      // todo отрефачить все это гавно ^^
      console.log(error);
      alert(error);
    }

    // replce главная страница ?
  };

  if (user !== null) {
    return <Main />;
  }

  return (
    <>
      <h1>Регистрация</h1>

      <form onSubmit={handleSubmit(submitFunc)}>
        <label className="label">
          Имя
          <input
            type="text"
            placeholder="Имя"
            {...register("name", {
              required: { value: true, message: "Укажите ваше имя..." },
              minLength: {
                value: 3,
                message: "Минимальная длина имени 3 - символа",
              },
            })}
          />
        </label>
        <p className="error">{errors.name && errors.name.message}</p>

        <label className="label">
          Электронная почта
          <input
            type="text"
            placeholder="Email"
            {...register("email", {
              required: { value: true, message: "Email адрес обязателен" },
              pattern: {
                value: /@/,
                message: "Email адрес должен содержать символ @",
              },
            })}
          />
        </label>
        <p className="error">{errors.email && errors.email.message}</p>

        <label className="label">
          Пароль
          <input
            type="password"
            {...register("password", {
              required: { value: true, message: "Укажите пароль..." },
              minLength: {
                value: 8,
                message: "Минимальная длина пароля 8 - символов",
              },
              // pattern: {
              //   value: /([A-Z])([0-9])+/g,
              //   message: "Пароль должен содержать хотябы одну заглавную букву",
              // },
            })}
          />
        </label>
        <p className="error">{errors.password && errors.password.message}</p>
        <input type="submit" disabled={!isValid} />
      </form>
    </>
  );
};

export default RegisterForm;
