import { useAppDispatch, useAppSelector } from "../../store";
import {
  setSearchTextAction,
  setSortByAscAction,
  setSortByDescAction,
} from "../../store/reducers/reducer";
import { IProduct } from "../../types";
import { Product } from "./Product";
import "./product.css";

export const ProductsList = () => {
  const products = useAppSelector((state) => state.products);
  const sortBy = useAppSelector((state) => state.sortBy);
  const searchText = useAppSelector((state) => state.searchText);
  const selectedCategory = useAppSelector(
    (state) => state.selectedCategoryName
  );
  const dispatch = useAppDispatch();
  let sortedProducts: IProduct[] = products;

  if (selectedCategory) {
    sortedProducts = sortedProducts.filter((product) => {
      return selectedCategory === product.category;
    });
  }

  if (searchText !== "") {
    sortedProducts = sortedProducts.filter((product) => {
      return product.name.toLowerCase().includes(searchText);
    });
  }

  if (sortBy === "asc") {
    sortedProducts = products.sort((a, b) => b.price - a.price);
  }

  if (sortBy === "desc") {
    sortedProducts = products.sort((a, b) => a.price - b.price);
  }

  const handleSearchTextChange = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    dispatch(setSearchTextAction(event.target.value.toLowerCase()));
  };

  return (
    <div className="product-list">
      <input
      className="product-list__input"
        onChange={handleSearchTextChange}
        type="search"
        placeholder="Search..."
      />
      <button className="pro" onClick={() => dispatch(setSortByAscAction())}>High</button>
      <button className="pro" onClick={() => dispatch(setSortByDescAction())}>Low</button>
      {sortedProducts.map((product) => (
        <Product key={product._id} product={product} />
      ))}
    </div>
  );
};
