export interface IUser {
  email: string;
  name: string;
  isAdmin: boolean;
}

export interface IRegisterData extends Omit<IUser, "isAdmin"> {
  password: string;
}

// state
export interface IState {
  user: IUser | null
}

export enum ActionTypes {
  SetUser = "set-user"
  
}

export interface SetUser {
  type: ActionTypes.SetUser;
  payload: IUser | null
}

export type Action = SetUser

export interface ICategory {
  name: string;
}

interface IProduct {
  name: string;
  price: number;
  amount: number;
  description: string;
  image: string;
  category: ICategory;
}
