import { ICategory, IProduct, IUser } from "./../../types";
import {
  IState,
  ActionTypes,
  SetUser,
  Action,
  SetProducts,
  SetCategories,
  SetByAscendingProducts,
  SetByDescendingProducts,
} from "../types";

const initialState: IState = {
  user: null,
  products: [],
  categories: [],
};

export const reducer = (state = initialState, action: Action): IState => {
  switch (action.type) {
    case ActionTypes.SetUser:
      return { ...state, user: action.payload };
    case ActionTypes.SetProducts:
      return { ...state, products: action.payload };
    case ActionTypes.SetCategories:
      return { ...state, categories: action.payload };
    case ActionTypes.SortByAscending:
      return { ...state, products: [...action.payload].sort((a,b)=> b.price - a.price) };
    case ActionTypes.SortByDescending:
      return { ...state, products: [...action.payload].sort((a,b)=> a.price - b.price) };
    default:
      return state;
  }
};

export const setUserAction = (payload: IUser): SetUser => ({
  type: ActionTypes.SetUser,
  payload,
});

export const setProductsAction = (payload: IProduct[]): SetProducts => ({
  type: ActionTypes.SetProducts,
  payload,
});

export const setCategoriesAction = (payload: ICategory[]): SetCategories => ({
  type: ActionTypes.SetCategories,
  payload,
});

export const setSortByAscendingAction = (payload: IProduct[]): SetByAscendingProducts => ({
  type: ActionTypes.SortByAscending,
  payload,
});

export const setSortByDescendingAction = (payload: IProduct[]): SetByDescendingProducts => ({
  type: ActionTypes.SortByDescending,
  payload,
});
