import React from "react";
import { useForm } from "react-hook-form";
import { IRegisterData } from "./types";
import config from "./configServer.json";
import jwtParse from "jwt-decode";

const RegisterForm = () => {
  const {
    register,
    handleSubmit,
    reset,
    formState: { errors, isValid },
  } = useForm<IRegisterData>({ mode: "onChange" });

  const submitFunc = async (data: IRegisterData) => {
    console.log(data);

    // запрос sign-up / sign-in
    try {
      const response = await fetch(config.endPoint + "/auth/sign-up", {
        method: "POST",
        headers: {
          "Content-Type": "application/json;charset=utf-8",
        },
        body: JSON.stringify(data),
      });
      const user = await response.json();
      // получаем jwt и добываем из него User(убрать лишнее)
      console.log(jwtParse(user.refreshToken));
    } catch (error) {
      console.log(error);
    }

    // сохраняем jwt в localstorage
    // User сетим в state
    // replce главная страница ?
    reset();
  };

  return (
    <>
      <h1>Регистрация</h1>

      <form onSubmit={handleSubmit(submitFunc)}>
        <label className="label">
          Имя
          <input
            type="text"
            placeholder="Имя"
            {...register("name", {
              required: { value: true, message: "Укажите ваше имя..." },
              minLength: {
                value: 3,
                message: "Минимальная длина имени 3 - символа",
              },
            })}
          />
        </label>
        <p className="error">{errors.name && errors.name.message}</p>

        <label className="label">
          Электронная почта
          <input
            type="text"
            placeholder="Email"
            {...register("email", {
              required: { value: true, message: "Email адрес обязателен" },
              pattern: {
                value: /@/,
                message: "Email адрес должен содержать символ @",
              },
            })}
          />
        </label>
        <p className="error">{errors.email && errors.email.message}</p>

        <label className="label">
          Пароль
          <input
            type="text"
            {...register("password", {
              required: { value: true, message: "Укажите пароль..." },
              minLength: {
                value: 8,
                message: "Минимальная длина пароля 8 - символов",
              },
              // pattern: {
              //   value: /([A-Z])([0-9])+/g,
              //   message: "Пароль должен содержать хотябы одну заглавную букву",
              // },
            })}
          />
        </label>
        <p className="error">{errors.password && errors.password.message}</p>
        <span className="radio-text">Выберете пол</span>
        <div className="radio">
          <label className="radio-text">
            male
            <input
              type="radio"
              {...register("gender", {
                required: { value: true, message: "Укажите ваш пол" },
              })}
              value="male"
            />
          </label>
          <label className="radio-text">
            female
            <input
              type="radio"
              {...register("gender", {
                required: { value: true, message: "Укажите ваш пол" },
              })}
              value="female"
            />
          </label>
          <label className="radio-text">
            other
            <input
              type="radio"
              {...register("gender", {
                required: { value: true, message: "Укажите ваш пол" },
              })}
              value="other"
            />
          </label>
        </div>
        <input type="submit" disabled={!isValid} />
      </form>
    </>
  );
};

export default RegisterForm;
