import { useAppSelector } from "../store";
import { IProduct } from "../types";

export const Basket = () => {
  const products = useAppSelector((state) => state.products);
  const productInBasket = useAppSelector(
    (state) => state.productInBasketIdList
  );

  const filteredProductsById = products.reduce((newArr: IProduct[], p) => {
    productInBasket.filter((k) => {
      if (k === p._id) {
        return newArr = [...newArr, p];
      }
    });
    return newArr;
  }, []);
  console.log(filteredProductsById);

  return (
    <div>
      1<span>Basket</span>
    </div>
  );
};

