import "./product.css";
import { IProduct } from "../../types";

interface IProps {
  product: IProduct;
}

export const Product = (props: IProps) => {
  return (
    <>
      <div className="product-card">
        <img
          className="product-card__image"
          src={props.product.image}
          alt="Картинка"
        ></img>
        <span className="product-card__name">{props.product.name}</span>
        <span className="card-product__price">{props.product.price}</span>
        <button className="card-product__button">Открыть-карточку</button>
      </div>
    </>
  );
};
