import { useAppDispatch, useAppSelector } from "../../store";
import { setSelectedCategoryAction } from "../../store/reducers/reducer";
import { ICategory } from "../../types";

interface IProps {
  category: ICategory;
}

export const Category = (props: IProps) => {
  const dispatch = useAppDispatch();
  let selectedCategory = useAppSelector((state) => state.selectedCategoryName);
  const handleSelectedCategory = (id: string) => {
    dispatch(setSelectedCategoryAction(id));
  };
  return (
    <button onClick={() => handleSelectedCategory(props.category._id)}>
      {props.category.name}
    </button>
  );
};
